frappe.ui.form.on('Supplier', {
	refresh(frm) {
		$(".form-shared").hide();
		$(".form-tags").hide();
		$(".form-sidebar-stats").hide();
		// 		$(".list-unstyled.sidebar-menu.text-muted").hide();

		if (frappe.user.has_role("Migoo Approver") === true) {
			console.log("Migoo Approver");
			frm.add_custom_button(__('Create_User'),
				function () {
					frappe.new_doc("User",
						{
							"username": frm.doc.email,
							"first_name": frm.doc.supplier_name,
							"role_profile_name": "Vendor",
							"module_profile": "Vendor",
							"middle_name": frm.doc.name,
							"mobile_no": frm.doc.mobile_number,
							"phone": frm.doc.phone_no,
						});
				});
		}

		if (frappe.user.has_role("Vendor") === true) {
			cur_frm.set_df_property("verify", "hidden", 1);
			$(".form-assignments").hide();
			$(".form-attachments").hide();
			$(".form-shared").hide();
			$(".form-tags").hide();
			$(".form-sidebar-stats").hide();
			$(".list-unstyled.sidebar-menu.text-muted").hide();
		}
		else {
			cur_frm.set_df_property("verify", "hidden", 0);
		}
		// $(".form-assignments").hide();
		// 		$(".form-attachments").hide();
		if (frappe.user.has_role("Vendor") === false) {
			frm.add_custom_button(__('Equipment'),
				function () {

					frappe.new_doc("Item", {
						"supplier": frm.doc.name,
					});
				});
		}
	},


	before_save: function (frm) {
		if (cur_frm.doc.verify == "Not Verify") {
			cur_frm.set_value("disabled", 1);
			cur_frm.set_df_property("disabled", "read_only", 1);
		}

		if (cur_frm.doc.verify == "Verify") {
			// cur_frm.set_value("disabled","");
			cur_frm.set_df_property("disabled", "read_only", 0);
		}

		var nm = frm.doc.name_of_suppier
		cur_frm.set_value("supplier_name", nm)
		// alert(frm.doc.supplier_name)

		if (frappe.user.has_role("Vendor") === true) {
			frm.set_value("verify", "Not Verify")
		}
		else {
			frm.set_value("verify", "Verify")
		}
	}

});

frappe.ui.form.on('Supplier', {
	refresh:function(frm) {

	   if(frappe.user.has_role("Vendor") === true)
	     {
	        setTimeout(() => {
	        console.log("View Hide");
	        frm.remove_custom_button('Accounting Ledger',"View"); 
	        frm.remove_custom_button('Accounts Payable',"View"); 
	        frm.remove_custom_button('Bank Account',"Create"); 
	        frm.remove_custom_button('Pricing Rule',"Create"); 
	        frm.remove_custom_button('enable_common_party_accounting',"Actions"); 
	        frm.remove_custom_button('Get Supplier Group Details',"Actions"); 
	         },0);  
	    }
	    else  if(frappe.user.has_role("Migoo Approver") === true)
	     {
	        setTimeout(() => {
	        console.log("View Hide");
	        frm.remove_custom_button('Accounting Ledger',"View"); 
	        frm.remove_custom_button('Accounts Payable',"View"); 
	        frm.remove_custom_button('Bank Account',"Create"); 
	        frm.remove_custom_button('Pricing Rule',"Create"); 
	        frm.remove_custom_button('enable_common_party_accounting',"Actions"); 
	       // frm.remove_custom_button('Get Supplier Group Details',"Actions"); 
	         },0);  
	    }
	    else{
	        frm.add_custom_button(__('Accounting Ledger'), function () {
				frappe.set_route('query-report', 'General Ledger',
					{ party_type: 'Supplier', party: frm.doc.name });
			}, __("View"));

			frm.add_custom_button(__('Accounts Payable'), function () {
				frappe.set_route('query-report', 'Accounts Payable', { supplier: frm.doc.name });
			}, __("View"));

			frm.add_custom_button(__('Bank Account'), function () {
				erpnext.utils.make_bank_account(frm.doc.doctype, frm.doc.name);
			}, __('Create'));

			frm.add_custom_button(__('Pricing Rule'), function () {
				erpnext.utils.make_pricing_rule(frm.doc.doctype, frm.doc.name);
			}, __('Create'));

			frm.add_custom_button(__('Get Supplier Group Details'), function () {
				frm.trigger("get_supplier_group_details");
			}, __('Actions'));
	    }

	}
});

frappe.listview_settings['Supplier'] = {
	get_indicator: function(doc) {
		var indicator = [(doc.verify), frappe.utils.guess_colour(doc.verify), "verify,=," + doc.verify];
		if(doc.verify=="Verify") 
		{
			indicator[1] = "green";
		}
		if(doc.verify=="Not Verify") 
		{
			indicator[1] = "red";
		}
		return indicator;
	},
	onload:function(doc) 
	{
	    if(frappe.user.has_role("Vendor") === true){

	        $('.btn-primary').hide();
	    }    
	},
	refresh: function(listview) {
	    if (frappe.user.has_role("Vendor")===true){
    	    $(".btn-primary").hide();
	    }
	}
};

