frappe.ui.form.on('Sales Invoice', {
	refresh: function (frm) {
		setTimeout(() => {
			frm.remove_custom_button('Fetch Timesheet');
		}, 0.2);

		if (frm.doc.docstatus === 0 && !frm.doc.is_return) {
			frm.add_custom_button(__("Get Log Sheet"), function () {
				let d = new frappe.ui.Dialog({
					title: __("Get Log Sheet"),
					fields: [
						{
							"label": __("From"),
							"fieldname": "from_time",
							"fieldtype": "Date",
							"reqd": 1,
						},
						{
							"label": __("Equipment"),
							"fieldname": "equipment",
							"fieldtype": "Link",
							"options": "Item",
							"reqd": 1
						},

						{
							fieldtype: "Column Break",
							fieldname: "col_break_1",
						},
						{
							"label": __("To"),
							"fieldname": "to_time",
							"fieldtype": "Date",
							"reqd": 1,
						},
						{
							"label": __("Customer"),
							"fieldname": "customer",
							"fieldtype": "Link",
							"options": "Customer",
							"reqd": 1,
							"default": frm.doc.customer
						},

					],
					primary_action: function () {
						const data = d.get_values();
						frm.events.get_sheet_data(frm, data);
						d.hide();

					},

					primary_action_label: __("Get Data")
				});
				d.show();
			});
		}
		var time = 0.00;
		var min = 0.00;
		var total1 = 0
		var main_total = 0
		var main_time = 0
		var main_time1 = 0
		var main_min = 0
		$.each(frm.doc.log_sheet_details || [], function (i, d) {
			var min1 = parseInt(d.total_working_time.split(':')[1]);
			var a = parseInt(d.total_working_time);
			var equipment = d.equipment_name;
			$.each(frm.doc.items || [], function (i, d) {
				var item = d.item_code;

				if (equipment == item) {
					//   alert(d.qty)
					if (d.qty == 0) {
						time = 0;
						time1 = 0;
						min = 0;
						total = 0;
						total1 = 0;
						time = time + a;
						var time1 = time * 60;
						min = min + min1;
						var total = time1 + min;
						total1 = total / 60;
						d.hours = total1;

					}
					else {
						time = time + a;
						var time1 = time * 60;
						min = min + min1;
						var total = time1 + min;
						total1 = total / 60;

						d.hours = total1;
					}
				}
				main_time = main_time + a;
				var main_time1 = time * 60;
				main_min = main_min + main_min;
				var total = main_time1 + min;
				main_total = total / 60;
			});
		});
	},
	get_sheet_data(frm, data) {
		return frappe.call({
			method: "get_customer_wise_sheet",
			args: {
				"name": frm.doc.name,
				"from_time": data.from_time,
				"to_time": data.to_time,
				"equipment": data.equipment,
				"customer": data.customer,
			}
		}).then(r => {
			window.location.reload()
		});
	},
	before_submit: function (frm) {
		var main_total = 0
		var main_time = 0
		var main_time1 = 0
		var main_min = 0
		$.each(frm.doc.log_sheet_details || [], function (i, d) {
			var breck_min1 = parseInt(d.idle_hours);
			var aa = parseInt(d.breck_down_hours);

			main_time = main_time + aa;

			main_min = main_min + breck_min1;
		})
		frm.set_value("total_breakdown_hours", main_time)
		frm.set_value("total_idle_hours", main_min)
	}
});

